import { defineStore } from 'pinia';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import VueJwtDecode from 'vue-jwt-decode';
import { useJwt } from '.';

export const useAuthStore = defineStore('auth', {
  state: () => ({
    user: null,
    accessToken: null,
    refreshToken: null,
  }),
  actions: {
    async login(credentials) {
      try {
        const response = await axios.post('/api/v1/token/', credentials);
        this.accessToken = response.data.access;
        this.refreshToken = response.data.refresh;

        console.log(response);

        // Decode token safely
        try {
          const tokenPayload = VueJwtDecode.decode(this.accessToken);
          const {header, payload} = useJwt(tokenPayload);
          console.log('Decoded Token Payload:', payload);
          console.log(header);

          // Set user details
          this.user = {
            first_name: tokenPayload.first_name,
            last_name: tokenPayload.last_name,
            email: tokenPayload.email,
          };
        } catch (error) {
          console.error('Error decoding token:', error);
          throw new Error('Invalid token received from server');
        }

        // Save tokens in localStorage for persistence
        localStorage.setItem('accessToken', this.accessToken);
        localStorage.setItem('refreshToken', this.refreshToken);
      } catch (error) {
        console.error('Login failed', error);
        throw error;
      }
    },
    logout() {
      this.user = null;
      this.accessToken = null;
      this.refreshToken = null;
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');
    },
    loadTokensFromStorage() {
      this.accessToken = localStorage.getItem('accessToken');
      this.refreshToken = localStorage.getItem('refreshToken');

      if (this.accessToken) {
        try {
          const tokenPayload = jwtDecode(this.accessToken);
          this.user = {
            first_name: tokenPayload.first_name,
            last_name: tokenPayload.last_name,
            email: tokenPayload.email,
          };
        } catch (error) {
          console.error('Error decoding stored token:', error);
          this.logout(); // Clear invalid tokens
        }
      }
    },
  },
});
