// src/stores/auth.js
import { defineStore } from 'pinia';
import axios from 'axios';

export const useAuthStore = defineStore('auth', {
  state: () => ({
    user: null,
    accessToken: null,
    refreshToken: null,
  }),
  actions: {
    async login(credentials) {
      try {
        const response = await axios.post('/api/v1/token/', credentials);
        this.accessToken = response.data.access;
        this.refreshToken = response.data.refresh;

        // Decode token payload if you want user info
        const tokenPayload = JSON.parse(atob(this.accessToken.split('.')[1]));
        this.user = tokenPayload;

        // Save tokens in localStorage for persistence
        localStorage.setItem('accessToken', this.accessToken);
        localStorage.setItem('refreshToken', this.refreshToken);
      } catch (error) {
        console.error('Login failed', error);
        throw error;
      }
    },
    logout() {
      this.user = null;
      this.accessToken = null;
      this.refreshToken = null;
      localStorage.removeItem('accessToken');
      localStorage.removeItem('refreshToken');
    },
    loadTokensFromStorage() {
      this.accessToken = localStorage.getItem('accessToken');
      this.refreshToken = localStorage.getItem('refreshToken');

      if (this.accessToken) {
        const tokenPayload = JSON.parse(atob(this.accessToken.split('.')[1]));
        this.user = tokenPayload;
      }
    },
  },
});
